class Solution(object):
    def characterReplacement(self, s, k):
        """
        :type s: str
        :type k: int
        :rtype: int
        """
        length = len(s)
        if length <= k + 1:
            return length
        tempS = s
        temp = 1
        left = 0
        right = k + 1
        dic = {}
        label = False
        now = -float('inf')
        for i in xrange(left,right):
            if tempS[i] in dic:
                dic[tempS[i]] += 1
                if dic[tempS[i]] > temp:
                    now = max(now,dic[tempS[i]])
                    label = True
            else:
                dic[tempS[i]] = 1
        breakLabel = False
        while right < length:
            while label:
                gap = now - temp
                gap2 = length - right
                right = right + gap
                if right >= length:
                    breakLabel = True
                    right = length
                    gap = gap2
                label = False
                temp = now
                for i in xrange(right-gap,right):
                    if tempS[i] in dic:
                        dic[tempS[i]] += 1
                        if dic[tempS[i]] > temp:
                            now = max(now,dic[tempS[i]])
                            label = True
                    else:
                        dic[tempS[i]] = 1
            if breakLabel:
                if label:
                    temp = now
                break
            if right < length:
                dic[tempS[left]] -= 1
                if dic[tempS[right]] in dic:
                    dic[tempS[right]] += 1
                else:
                    dic[tempS[right]] = 1
                left += 1
                right += 1
                for item in dic:
                    if dic[item] > temp:
                        now = max(temp,dic[item])
                        label = True

        return min(temp + k, length)
